  return (
    <section className="login__container">

      <form className="login__form" onSubmit={handleSubmit}>
        <h3 className="login__title">Регистрация</h3>
        <input
          required
          autoComplete="on"
          name="email"
          type="email"
          minLength="5"
          maxLength="40"
          className="login__input"
          placeholder="Email" 
          //value={login.email || ""} 
          onChange={handleChange}
        />
        <span className="login__error login__error_visible name-error" />
        <input
          required
          autoComplete="on"
          name="password"
          type="password"
          minLength="5"
          maxLength="5"
          className="login__input"
          placeholder="Пароль" 
         // value={login.password || ""}
          //htmlFor="confirmPassword"
          onChange={handleChange}
        />
        <span className="login__error login__error_visible password-error" />
        <button type="submit" 
                className="login__button">
                Зарегистрироваться
                </button>
      </form>

      <p className="login__subtitle">Уже зарегистрированы?<Link to="/login" className="login__subtitle"> Войти</Link></p>

    </section>  


    );
  }

export default withRouter(Register);
