import React from 'react';
//import {CurrentUserContext} from '../contexts/CurrentUserContext';
import { Link, withRouter } from 'react-router-dom';



function Register() {

  const [email, setEmail] = React.useState(''); 
  const [password, setPassword] = React.useState('');
  const [confirmPassword, setConfirmPassword] = React.useState('');

  function handleChange(e){
    const {email, value} = e.target;
    this.setEmail({
      [email]: value 
    });
  }

  function handleSubmit(e){
        e.preventDefault()
    if (password === confirmPassword){
      // сюда добавим логику обработки формы регистрации
    }
  }

  return (
    <section className="login__container">

      <form className="login__form">
        <h3 className="login__title">Регистрация</h3>
        <input
          required
          name="email"
          type="url"
          minLength="5"
          //maxLength="40"
          className="login__input"
          placeholder="Email" 
          value={email || ""} 
          onChange={handleChange}
        />
        <span className="login__error login__error_visible name-error" />
        <input
          required
          name="password"
          type="password"
          minLength="5"
          maxLength="5"
          className="login__input"
          placeholder="Пароль" 
          value={password || ""}
          onChange={handleChange}
        />
        <span className="login__error login__error_visible password-error" />
        <button type="submit" 
                className="login__button">Зарегистрироваться</button>
      </form>

      <p>Уже зарегистрированы?<Link to="/login"> Войти</Link></p>

    </section>  


    );
  } 

export default withRouter(Register);


