import Header from './Header';
import Main from './Main';
import Footer from './Footer';


function App(props) {

  function handleEditProfileClick() {
    const profilePopup = document.querySelector('#popup-profile');
    profilePopup.classList.add('popup_opened');
  };

  function handleAddPlaceClick() {
    const addPicturePopup = document.querySelector('#popup-AddPicture');
    addPicturePopup.classList.add('popup_opened');
  };

  function handleEditAvatarClick() {
    const avatarPopup = document.querySelector('#update_avatar');
    avatarPopup.classList.add('popup_opened');
  };

/*
 const isEditProfilePopupOpen = profilePopup.classList.contains('popup_opened');
 const isAddPlacePopupOpen = addPicturePopup.classList.contains('popup_opened');
 const isEditAvatarPopupOpen = avatarPopup.classList.contains('popup_opened');




const element = document.querySelector('#myElement');
 let isClicked = false;

element.addEventListener('click', () => {
    isClicked = true;
}); 




 {isEditAvatarPopupOpen
   ?avatarPopup.classList.remove('popup_opened')
   :avatarPopup.classList.add('popup_opened');}

 this._popup = document.querySelector(popupSelector);

isOpen (() => {
  this._popup.classList.add('popup_opened');
});

onEditProfile (() => {
  isEditProfilePopupOpen = true;
}); 

onEditProfile (() => {
  isAddPlacePopupOpen = true;
});

onEditAvatar (() => {
  isEditAvatarPopupOpen = true;
});*/

  return (
    <div className="body">
    <div className="page">
      <Header />
      <Main />
      <Footer />
      <template id="cards-template">
          <div className="card">
            <div className="card__img-container">
              <img className="card__image" />
              <button className="card__delete-button" type="button"></button>
            </div>
            <div className="card__container">
              <h2 className="card__text"></h2>
              <div className="card__likes-container">
              <button className="card__like-button" type="button"></button>
              <p className="card__counter">0</p>
              </div>
            </div>
          </div>
        </template>

        <aside id="popup-profile" className="popup">
           <div className="popup__container">
          <button className="popup__close-button" type="button"></button>

            <form id="popupProfileForm" className="popup__form popup__form_profile-disabled" name="popupProfileForm" noValidate>
              <h3 className="popup__title">Редактировать профиль</h3>

                <input
                  required
                  id="name"
                  name="name"
                  type="text"
                  minLength="2"
                  maxLength="40"
                  className="popup__input"
                  placeholder="Имя"/>
                <span className="popup__error popup__error_visible name-error"></span>

                <input
                  required
                  id="job"
                  name="about"
                  type="text"
                  minLength="2"
                  maxLength="200"
                  className="popup__input"
                  placeholder="Профессиональная деятельность" />

                <span className="popup__error popup__error_visible job-error"></span>

                <button
                  id="button-submit"
                  type="submit"
                  className="popup__button">Сохранить</button>
            </form>
           </div>
        </aside>

        <aside id="popup-AddPicture" className="popup">
          <div className="popup__container">
         <button id="addPicturePopapCloseButton" className="popup__close-button" type="button"></button>

           <form id="popupForm" className="popup__form" name="popupAddPictureForm" noValidate>
             <h3 className="popup__title">Новое место</h3>

              <input
                id="nameOfPicture"
                name="name"
                type="text"
                minLength="2"
                maxLength="30"
                placeholder="Название"
                className="popup__input"
                required />
              <span className="popup__error popup__error_visible nameOfPicture-error"></span>

              <input
                id="linkOfPicture"
                name="link"
                type="url"
                placeholder="Ссылка на картинку"
                className="popup__input"
                required />
              <span id="spanOfPicture" className="popup__error popup__error_visible linkOfPicture-error"></span>

              <button
                id="makeButton"
                type="submit"
                className="popup__button popup__button_block">Создать</button>
           </form>
          </div>
        </aside>

       <aside id="popup-fullSizePicture" className="popup">
         <div className="popup__picture-container">
          <button id="fullSizePicturePopapCloseButton" className="popup__close-button" type="button"></button>
        <div>
          <img className="popup__picture" />
          <p className="popup__picture-name"></p>
        </div>
         </div>
       </aside>

       <aside id="areYouSureToDelete" className="popup">
        <div className="popup__container">
       <button id="areYouSureToDelete_closeButton" className="popup__close-button" type="button"></button>
         <form id="popupFormАreYouSureToDelete" className="popup__form" name="popup_areYouSureToDelete" noValidate>
           <h3 className="popup__title">Вы уверены?</h3>
            <button
              id="popup_areYouSureToDelete_button"
              type="submit"
              className="popup__button popup__yes-button">Да</button>
         </form>
        </div>
       </aside>

       <aside id="update_avatar" className="popup">
        <div className="popup__container">
       <button id="update_avatar_popapCloseButton" className="popup__close-button" type="button"></button>

         <form id="popupFormAvatarUpdate" className="popup__form popup__update-avatar" name="new_avatar_form" noValidate>
           <h3 className="popup__title">Обновить аватар</h3>
           <input
           id="avatar-link"
           name="avatar"
           type="url"
           placeholder="Ссылка на картинку"
           className="popup__input popup__avatar-link"
           required />
            <span className="popup__error popup__error_visible avatar-link-error"></span>
            <button
              type="submit"
              className="popup__button popup__button_block">Сохранить</button>
         </form>
        </div>
       </aside>

    </div>
    </div>
  );
}

export default App;
