import React from 'react';
//import React, { Button } from 'react';
//import {CurrentUserContext} from '../contexts/CurrentUserContext';
import { Link, withRouter, useHistory} from 'react-router-dom';
import * as Auth from './Auth';


function Register({handleSubmit}) {
  const [login, setLogin] = React.useState({ email: "", password: "" });
  const history = useHistory(); 

  function handleChange(e) {
    const { value, name } = e.target;
    setLogin({ ...login, [name]: value });
  }

  function handleSubmit(e){
    e.preventDefault();
    Auth.register({
      email: login.email,
      password: login.password,
    })
    .then((res) => {
      history.push('/login');
    })
}

  return (
    <section className="login__container">

      <form className="login__form" onSubmit={handleSubmit}>
        <h3 className="login__title">Регистрация</h3>
        <input
          required
          autoComplete="on"
          name="email"
          type="email"
          minLength="5"
          //maxLength="40"
          className="login__input"
          placeholder="Email" 
          //value={login.email || ""} 
          onChange={handleChange}
        />
        <span className="login__error login__error_visible name-error" />
        <input
          required
          autoComplete="on"
          name="password"
          type="password"
          minLength="5"
          maxLength="5"
          className="login__input"
          placeholder="Пароль" 
         // value={login.password || ""}
          //htmlFor="confirmPassword"
          onChange={handleChange}
        />
        <span className="login__error login__error_visible password-error" />
        <button type="submit" 
                className="login__button">
                Зарегистрироваться
                </button>
      </form>

      <p className="login__subtitle">Уже зарегистрированы?<Link to="/login" className="login__subtitle"> Войти</Link></p>

    </section>  


    );
  }

export default withRouter(Register);
