import React from 'react';
import {CurrentUserContext} from '../contexts/CurrentUserContext'

function EditProfilePopup({onClose, isOpen, onUpdateUser}) {
  const [name, setName] = React.useState('');
  const [description, setDescription] = React.useState('');
  const currentUser = React.useContext(CurrentUserContext);
  React.useEffect(() => {
    setName(currentUser.name);
    setDescription(currentUser.about);
  }, [currentUser]);
  function handleNameChange(e) {
    setName(e.target.value);
  }
  function handleDescriptionChange(e) {
    setDescription(e.target.value);
  }  
  function handleSubmit(e) {
    e.preventDefault();
    // Передаём значения управляемых компонентов во внешний обработчик
    onUpdateUser({
        name,
        about: description,
    });
  } 
    return (
      <aside className={`popup ${isOpen && 'popup_opened'}`}>
        <div className="popup__container">
          <button className="popup__close-button" type="button" onClick={onClose}></button>
          <form className="popup__form" name="popup-profile" noValidate>
            <h3 className="popup__title">Редактировать профиль</h3>
            <input
                required
                id="name"
                name={name}
                type="text"
                minLength="2"
                maxLength="40"
                className="popup__input"
                placeholder="Имя" 
                value={name || ""} 
                onChange={handleNameChange}/>
            <span className="popup__error popup__error_visible name-error" />
            <input
                required
                id="job"
                name={description}
                type="text"
                minLength="2"
                maxLength="200"
                className="popup__input"
                placeholder="Профессиональная деятельность" 
                value={description || ""} 
                onChange={handleDescriptionChange}/>
            <span className="popup__error popup__error_visible job-error" />
            <button type="submit" 
                    className="popup__button"
                    onClick={handleSubmit}>Сохранить</button>
          </form>
        </div>
      </aside>
  );
}

export default EditProfilePopup;
