import React from 'react';
import headerLogo from '../images/logo.svg';
import { Link } from 'react-router-dom';
import {CurrentUserContext} from '../contexts/CurrentUserContext'

function Header({loggedIn, userEmail, signOut}) {

  const currentUser = React.useContext(CurrentUserContext);

  return (
    <header className="header">
        <a href="#">
          <img src={headerLogo} alt="Логотип" className="header__logo" />
        </a>

        <ul className="header__navBar">
          <li className="header__nav">{currentUser.userEmail}</li>
          <li className="header__nav">{loggedIn? <button className="header__exit-button" onClick={signOut}>Выйти</button> : <Link to="/register" className="header__nav-link">Регистрация</Link>}</li>
        </ul>
    </header>
  );
}

export default Header;


