import React from 'react';

function EditAvatarPopup({isOpen, onClose, onUpdateAvatar}) {

  const editAvatarRef = React.useRef('');

  function handleSubmit(e) {
    e.preventDefault();
    onUpdateAvatar({
      avatar: editAvatarRef.current.value/* Значение инпута, полученное с помощью рефа */
    });

  React.useEffect(() => {
    editAvatarRef.current.value = '';
  }, [isOpen])

  } 
    return (
      <aside className={`popup ${isOpen && 'popup_opened'}`}>
        <div className="popup__container">
          <button className="popup__close-button" type="button" onClick={onClose}></button>
          <form className="popup__form" name="update-avatar" noValidate>
            <h3 className="popup__title">Обновить аватар</h3>
            <input
                id="avatar-link"
                name="avatar"
                type="url"
                placeholder="Ссылка на картинку"
                className="popup__input popup__avatar-link"
                required 
                ref={editAvatarRef}/>
            <span className="popup__error popup__error_visible avatar-link-error"></span>
            <button type="submit" 
                    className="popup__button"
                    onClick={handleSubmit}>Сохранить</button>
          </form>
        </div>
      </aside>
  );
}

export default EditAvatarPopup;
