import React from 'react';
//import {CurrentUserContext} from '../contexts/CurrentUserContext';
import { Link, withRouter } from 'react-router-dom';
import * as Auth from './Auth';
import { useParams, useHistory } from 'react-router-dom'; 


function Login({handleLogin}) {

  const [login, setLogin] = React.useState({ email: "", password: "" });
  const history = useHistory();

  function handleChange(e) {
    const {name, value} = e.target;
    setLogin({
      [name]: value 
    });
  }
  function handleSubmit(e){
    e.preventDefault();
  if (!login.email || !login.password){
    return;
  }
  Auth.authorize(login.email, login.password)
  .then((data) => {
    if (data.jwt){
      this.setLogin({email: '', password: ''} ,() => {
          handleLogin();
          history.push('/ducks');
      })
    }  
  })
  .catch(err => console.log(err)); // запускается, если пользователь не найден
} 

  return ( 
      <section className="login__container">

          <form className="login__form" onSubmit={handleSubmit}>
            <h3 className="login__title">Вход</h3>
            <input
        required
        name="email"
        type="email"
        minLength="5"
        className="login__input"
        placeholder="Email" 
       //value={email || ""} 
        />
      <span className="login__error login__error_visible name-error" />
      <input
        required
        name="password"
        type="password"
        minLength="5"
        maxLength="5"
        className="login__input"
        placeholder="Пароль" 
        //htmlFor="current-password"
        //value={description || ""}
        />
      <span className="login__error login__error_visible password-error" />
            <button type="submit" 
                    className="login__button">Войти</button>
          </form>

      </section>
    );
  } 

export default withRouter(Login);
