import React from 'react';
import {CurrentUserContext} from '../contexts/CurrentUserContext'

function AddPlacePopup({isOpen, onClose, onAddPlace}) {
  const [values, setValues] = React. useState({});
  const [name, setName] = React.useState('');
  const [link, setLink] = React.useState('');
  const currentUser = React.useContext(CurrentUserContext);
  React.useEffect(() => {
    setName(values.name);
    setLink(values.link);
  }, [values]);
  function handleNameChange(e) {
    setName(e.target.value);
  }
  function handleLinkChange(e) {
    setLink(e.target.value);
  }  
   function handleSubmit(e) {
    e.preventDefault();
    onAddPlace(setValues({
        name,
        link
    }));;
   }

  return (
    <aside className={`popup ${isOpen && 'popup_opened'}`}>
      <div className="popup__container">
        <button className="popup__close-button" type="button" onClick={onClose}></button>
          <form className="popup__form" 
            name="popup-AddPicture"
            noValidate
            >
            <h3 className="popup__title">Новое место</h3>
            <input
              id="nameOfPicture"
              name="name"
              type="text"
              minLength="2"
              maxLength="30"
              placeholder="Название"
              className="popup__input"
              required 
              value={name}
              onChange={handleNameChange}/>
            <span className="popup__error popup__error_visible nameOfPicture-error" />
            <input
              id="linkOfPicture"
              name="link"
              type="url"
              placeholder="Ссылка на картинку"
              className="popup__input"
              required 
              value={link}
              onChange={handleLinkChange}/>
            <span id="spanOfPicture" className="popup__error popup__error_visible linkOfPicture-error" />
            <button type="submit" 
            className="popup__button"
            onClick={handleSubmit}>Создать</button>
          </form>
      </div>
    </aside>
  );
}

export default AddPlacePopup;
